import React, { useState } from 'react';
import axios from 'axios';

function ChatApp() {
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');

    const sendMessage = async () => {
        try {
            // Add the user's message to the messages array
            const updatedMessages = [...messages, { sender: 'user', text: inputMessage }];
            setMessages(updatedMessages);

            // Clear the input field
            setInputMessage('');

            // Make a request to the Flask API endpoint with the user's message
            const response = await axios.post('http://localhost:5000/chatbot', { message: inputMessage });
            const chatbotResponse = response.data.response;

            // Add the chatbot's response to the messages array
            const updatedMessagesWithBotResponse = [...updatedMessages, { sender: 'bot', text: chatbotResponse }];
            setMessages(updatedMessagesWithBotResponse);

        } catch (error) {
            console.error('Error sending message to chatbot:', error);
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-window">
                {/* Display the chat messages */}
                {messages.map((message, index) => (
                    <div key={index} className={`message ${message.sender}`}>
                        {message.text}
                    </div>
                ))}
            </div>
            <div className="input-box">
                {/* Input field for sending messages */}
                <input
                    type="text"
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                    placeholder="Type your message..."
                />
                {/* Button to send message */}
                <button onClick={sendMessage}>Send</button>
            </div>
        </div>
    );
}

export default ChatApp;
